import React from "react";
import {
    Image, Keyboard,
    KeyboardAvoidingView,
    ScrollView,
    StyleSheet,
    Text,
    TextInput,
    TouchableOpacity,
    View
} from 'react-native';
import { useUserContext } from "../context/UserContext";
import SplashScreen from "./SplashScreen";
const LoginScreen = () => {

    const auth = useUserContext();

    return (<View>
            <View style={styles.mainBody}>
                <ScrollView
                    keyboardShouldPersistTaps="handled"
                    contentContainerStyle={{
                        flex: 1,
                        justifyContent: 'center',
                        alignContent: 'center',
                    }}>
                    <View>
                        <KeyboardAvoidingView enabled>
                            <View style={{alignItems: 'center'}}>
                                <Image
                                    source={require('../../assets/images/splash.png')}
                                    style={{
                                        width: '50%',
                                        height: 100,
                                        resizeMode: 'contain',
                                        margin: 30,
                                    }}
                                />
                            </View>
                            <View style={styles.SectionStyle}>
                                <TextInput
                                    style={styles.inputStyle}
                                    onChangeText={(UserEmail) => {}
                                        //setUserEmail(UserEmail)
                                    }
                                    placeholder="Enter Email" //dummy@abc.com
                                    placeholderTextColor="#8b9cb5"
                                    autoCapitalize="none"
                                    keyboardType="email-address"
                                    returnKeyType="next"
                                    onSubmitEditing={() => {}
                                       // passwordInputRef.current &&
                                       // passwordInputRef.current.focus()
                                    }
                                    underlineColorAndroid="#f000"
                                    blurOnSubmit={false}
                                />
                            </View>
                            <View style={styles.SectionStyle}>
                                <TextInput
                                    style={styles.inputStyle}
                                    onChangeText={(UserPassword) => {}
                                       // setUserPassword(UserPassword)
                                    }
                                    placeholder="Enter Password" //12345
                                    placeholderTextColor="#8b9cb5"
                                    keyboardType="default"
                                    //ref={passwordInputRef}
                                    onSubmitEditing={Keyboard.dismiss}
                                    blurOnSubmit={false}
                                    secureTextEntry={true}
                                    underlineColorAndroid="#f000"
                                    returnKeyType="next"
                                />
                            </View>
                            {/*{errortext != '' ? (*/}
                            {/*    <Text style={styles.errorTextStyle}>*/}
                            {/*        {errortext}*/}
                            {/*    </Text>*/}
                            {/*) : null}*/}
                            <TouchableOpacity
                                style={styles.buttonStyle}
                                activeOpacity={0.5}>

                                <Text style={styles.buttonTextStyle}>LOGIN</Text>
                            </TouchableOpacity>
                            <Text
                                style={styles.registerTextStyle}>
                                Register
                            </Text>
                        </KeyboardAvoidingView>
                    </View>
                </ScrollView>
            </View>
    </View>)
}

export default LoginScreen;

const styles = StyleSheet.create({
    mainBody: {
        flex: 1,
        justifyContent: 'center',
        backgroundColor: '#307ecc',
        alignContent: 'center',
    },
    SectionStyle: {
        flexDirection: 'row',
        height: 40,
        marginTop: 20,
        marginLeft: 35,
        marginRight: 35,
        margin: 10,
    },
    buttonStyle: {
        backgroundColor: '#7DE24E',
        borderWidth: 0,
        color: '#FFFFFF',
        borderColor: '#7DE24E',
        height: 40,
        alignItems: 'center',
        borderRadius: 30,
        marginLeft: 35,
        marginRight: 35,
        marginTop: 20,
        marginBottom: 25,
    },
    buttonTextStyle: {
        color: '#FFFFFF',
        paddingVertical: 10,
        fontSize: 16,
    },
    inputStyle: {
        flex: 1,
        color: 'white',
        paddingLeft: 15,
        paddingRight: 15,
        borderWidth: 1,
        borderRadius: 30,
        borderColor: '#dadae8',
    },
    registerTextStyle: {
        color: '#FFFFFF',
        textAlign: 'center',
        fontWeight: 'bold',
        fontSize: 14,
        alignSelf: 'center',
        padding: 10,
    },
    errorTextStyle: {
        color: 'red',
        textAlign: 'center',
        fontSize: 14,
    },
});
